    func application(_ application: UIApplication, didReceiveRemoteNotification userInfo: [AnyHashable: Any], fetchCompletionHandler completionHandler: @escaping (_: UIBackgroundFetchResult) -> Void) {
        NotificationCenter.default.post(name: NSNotification.Name("CountReload"), object: nil)
        if UIApplication.shared.applicationState == .active {
            application.applicationIconBadgeNumber = 0;
            processAPN(userInfo)
            AudioServicesPlaySystemSound(1007)
        }
    }
    
    func processAPN(_ dict: [AnyHashable: Any]) {
        //    NSLog(@"NOTIFICATION :: %@", dict);
        let aps = dict["aps"] as? [AnyHashable: Any]
        print(aps ?? 0)
        if aps != nil {
            //let title = aps?["alert"]
            
            let apsDict = aps?.description
            print(apsDict ?? 0)
            
            //Get Title
            let arrTitle = apsDict?.components(separatedBy: "title")
            let arrGetTitle = arrTitle?.last?.components(separatedBy: ";")
            var strTitle = arrGetTitle?.first
            strTitle = strTitle?.replacingOccurrences(of: "=", with: "")
            strTitle = strTitle?.replacingOccurrences(of:"\"", with: "")
            strTitle = strTitle?.removeWhitespace()
            print(strTitle?.description ?? 0)
            
            //Get SubTitle
            let arrSubTitle = apsDict?.components(separatedBy: "body")
            let arrGetSubTitle = arrSubTitle?.last?.components(separatedBy: ";")
            var strSubTitle = arrGetSubTitle?.first
            strSubTitle = strSubTitle?.replacingOccurrences(of: "=", with: "")
            strSubTitle = strSubTitle?.replacingOccurrences(of:"\"", with: "")
            strSubTitle = strSubTitle?.removeWhitespace()
            print(strSubTitle?.description ?? 0)
            
            let banner = Banner(title: strTitle, subtitle: strSubTitle, image: #imageLiteral(resourceName: "icon"), backgroundColor: UIColor.white)
            banner.springiness = .none
            banner.position = .top
            banner.titleLabel.textColor = UIColor.black
            banner.detailLabel.textColor = UIColor.black
            banner.didTapBlock = {
                print("Banner was tapped on \(Date())!")
            }
            banner.show(duration: 3.0)
            
            //Constant.showAlert(title: "Steelbuddy Notification", message: (strTitle?.description)!)
        }
    }
    
    
